mapper.select:

SELECT item_name 
FROM Refrigerator
WHERE user_id = #{userId}
GROUP BY item_name;


UPDATE Refrigerator
SET purchased_yn = 'N', purchased_dt = null
WHERE idx = #{idx}


UPDATE Refrigerator
SET finish_yn = 'Y'
WHERE idx = #{idx}


SELECT type 
FROM Refrigerator
WHERE user_id = #{userID}
GROUP BY type;

mapper.update:

UPDATE Refrigerator
SET item_name = #{itemName}, amt = #{amt}, type = #{type} 
WHERE idx = #{idx}
