mapper.insert:

INSERT INTO WorkLog 
	(title
	, content
	, document_type
	, insert_dt
	, insert_user
	, cust_cd
	, receipt_dt
	, due_dt
	, compl_dt
	, compl_yn) 
VALUES(#{title}
	, #{content}
	, #{documentType}
	, now()
	, #{userId}
	, #{custCd}
	, #{receiptDt}
	, #{dueDt}
	, #{complDt}
	, #{complYn})

mapper.update:

UPDATE WorkLog
SET deleted_yn = "Y"
	, delete_dt = now()
	, delete_user = #{userId}
WHERE idx = ${idx}


UPDATE WorkLog
SET update_dt = now()
	, update_user = #{userId}
	, cust_cd = #{custCd}
	, document_type = #{documentType}
	, receipt_dt = #{receiptDt} 
	, due_dt = #{dueDt}
	, compl_dt = #{complDt}
	, compl_yn = #{complYn}
	, title= #{title}
	, content = #{content}   
WHERE idx = ${idx}


UPDATE PersetWorkLog
SET maxrow = #{maxrow}
	, cust_nm = #{custNm}
	, title = #{title}
	, content = #{content}
	, start_dt = #{startDt}
	, end_dt = #{endDt}
	, compl_yn = #{complYn}
	, order1 = #{order1}
	, order2 = #{order2}
	, orderby1 = #{orderby1}
WHERE user_id = #{userId}
